mapper.select:
SELECT FID AS fid, FNAME AS fname, FAMC AS famc, FRLVL AS frlvl, FTYPE AS ftype, FREFPR AS frefpr, FSETDT AS fsetdt, FTC AS ftc, FM1PR AS fm1pr, FM3PR AS fm3pr, FM6PR AS fm6pr, FM12PR AS fm12pr, FACMPR AS facmpr,
-- 모달용 추가 필드들 BASEDT AS basedt, EVALTYPE AS evaltype, MGMTCOMP AS mgmtcomp, -- 실제 컬럼명 확인(예: MGMT_COMP 등) GRADE3Y AS grade3y, GRADE5Y AS grade5y, RELATEDFUND AS relatedfund, INVESTREGION AS investregion, PAST2023 AS past2023, PAST2024 AS past2024, FEE1Y AS fee1y, FEE3Y AS fee3y, STARTINFO AS startinfo, SALESFEE AS salesfee, FAMILYSIZE AS familysize, TRUSTFEE AS trustfee, AUM AS aum, REDEEMFEE AS redeemfee, CHART1 AS chart1, CHART2 AS chart2
FROM FUND WHERE FID = #{fid}
